import React, { useState } from 'react';
import axios from 'axios';
import './App.css';


function Navbar() {

  const [isLoading, setIsLoading] = useState(false);
  const [userData, setUserData] = useState(null);
  const cards = {
    color: "white",
    backgroundColor: "DodgerBlue",

  }

  const fetchUsers = async () => {
    setIsLoading(true);

    try {
      const response = await axios.get('https://reqres.in/api/users?page=1');
      setUserData(response.data.data);
    } catch (error) {
      console.log(error);
    }

    setIsLoading(false);
  };

  return (
    <container>

      <nav className="navbar">
        <div className="navbar-brand">Brand Name</div>
        <button className="btn" onClick={fetchUsers}>Get Users</button>
      </nav>

      <div className='container'>
        {isLoading && <div className="loader btn-lg-primary">loader</div>}
        {userData && (
          <div className="user-container">
            {userData.map((user) => (
              <div className="user-card" key={user.id}>
                <div class="row">
                  <div class="col-lg-4 col-md-12 mb-4">
                    <div class="card">
                      <img src={user.avatar} class="w-100" />
                      <div class="card-body">
                        <a href="" class="text-reset">
                          <h5 class="card-title mb-3">{user.first_name} {user.last_name}</h5>
                        </a>
                        <a href="" class="text-reset">
                          <p>{user.email}</p>
                        </a>
                        <h6 class="mb-3">{user.email}</h6>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

    </container>
  );
}

export default Navbar;
